    $query = "SELECT aula.id_aula AS id, aula.dia, TIMEDIFF(aula.hora_termino, aula.hora_inicio) AS horas, professor.nome AS nome, turma.numero_turma AS numero FROM aula 
    INNER JOIN professor ON aula.id_professor = professor.id_professor INNER JOIN turma ON aula.id_turma = turma.id_turma 
    WHERE aula.id_professor = $id_professor AND (aula.dia >= '$inicio' AND aula.dia <= '$fim') ORDER BY id ASC"; 
    $result = mysqli_query($link, $query);
